import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import ItemInfo from "../components/ItemInfo/ItemInfo";
import { setProductDetail } from "../slices/productSlice";
import Navbar from "../components/navbar/Navbar";

export default function Detail() {
  const { id } = useParams();
  const dispatch = useDispatch();
  const productDetail = useSelector((state) => state.product.productDetail);

  useEffect(() => {
    getProduct();
  }, []);

  const getProduct = () => {
    try {
      fetch(`https://api.mercadolibre.com/items/${id}`)
        .then((res) => res.json())
        .then((result) => {
          dispatch(setProductDetail(result));
        });
    } catch (Exception) {}
  };


  const getDescription = () => {
    try {
      fetch(`https://api.mercadolibre.com/items/${id}/description`)
      .then((res) => res.json())
      .then((result) => {
        dispatch(setProductDescription(result.description));
      });

    }catch(Exception){

    }


  }


  return (
    <div>
       <p>Paula</p>
      <ItemInfo title={productDetail.title} thumbnail={productDetail.pictures[0].url} 
          price={productDetail.price} condition={productDetail.condition} />
    </div>
  );
}
