import React, { useEffect , useState } from "react";
import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import ItemInfo from "../components/ItemInfo/ItemInfo";
import { setProductDetail, setProductDescription } from "../slices/productSlice";
import Navbar from "../components/navbar/Navbar";

export default function Detail() {
  const { id } = useParams();
  const dispatch = useDispatch();
  const [urlId , setUrlId] = useState(undefined);
  const productDetail = useSelector((state) => state.product.productDetail);
  const productDescription = useSelector((state) => state.product.productDescription);

  useEffect(() => {
    getProduct();
    getDescription();
  }, []);

  const getProduct = () => {
    try {
      fetch(`https://api.mercadolibre.com/items/${id}`)
        .then((res) => res.json())
        .then((result) => {
          dispatch(setProductDetail(result));
        });
    } catch (Exception) {}
  };



 const getDescription = () => {
    try {
      fetch(`https://api.mercadolibre.com/items/${id}/description`)
      .then((res) => res.json())
      .then((result) => {
        dispatch(setProductDescription(result.plain_text));
        setUrlId

      });

    }catch(Exception){

    }

  }


  const entrar = (productDetail , productDescription) =>{

    if(productDetail.id = id){
      // <ItemInfo  thumbnail={productDetail.pictures[0].url} 
      // title={productDetail.title}
      // condition={productDetail.condition}
      // total_sold_items={productDetail.sold_quantity}
      // price={productDetail.price}
      // description={productDescription}
      // />
      console.log("Entro")
    }


  }


  return (
    <div>
      <Navbar/>
      {
        entrar(productDetail, productDescription)
      }
    </div>
  );
}
