import React from "react";
// import {
//     BrowserRouter as Router,
//     Switch,
//     Route,
//     Link,
//     useRouteMatch,
//     useParams
// } from "react-router-dom";
import "./itemInfo.scss";
import Free from "../../assets/ic_shipping@2x.png.png.png";
import formatPrice from "../../utils/format";

const ItemInfo = ({ item }) => {
  return (
    <div className="detail-container">
      <div className="detail-img">
        <img src={thumbnail} alt={title} />
      </div>

      <div className="detail-description">
        <div className="product-condition">
          {condition} - {total_items}
          {item.free_shipping && (
            <img
              src={Free}
              alt="logo"
              width="20"
              className="item-shipping-img"
              height="20"
            />
          )}
        </div>
        <div className="title">{title} </div>
        <div className="Price">{formatPrice(price)}</div>
        <button className="detail-btn">Comprar</button>
      </div>

      <div className="product-description">
        <div className="title-description">Descripci√≥n del producto</div>
        <div className="description">
        
          <p>{description}</p>{" "}
        </div>
      </div>
    </div>
  );
};

export default ItemInfo;
