import React, { useEffect , useState } from "react";
import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import ItemInfo from "../components/ItemInfo/ItemInfo";
import { setProductDetail, setProductDescription } from "../slices/productSlice";
import Navbar from "../components/navbar/Navbar";

export default function Detail() {
  const { id } = useParams();
  const dispatch = useDispatch();
  const [urlId , setUrlId] = useState(undefined);
   const [state, setState] = useState({});
  const productDetail = useSelector((state) => state.product.productDetail);
  const productDescription = useSelector((state) => state.product.productDescription);

  useEffect(() => {
    getProduct();
    return () => {
      setState({}); // This worked for me
    };
  }, []);

  const getProduct = () => {
    try {
      fetch(`https://api.mercadolibre.com/items/${id}`)
        .then((res) => res.json())
        .then((result) => {
          dispatch(setProductDetail(result));
          setUrlId(result)
       

        });
    } catch (Exception) {}
  };



 const getDescription = () => {
    try {
      fetch(`https://api.mercadolibre.com/items/${id}/description`)
      .then((res) => res.json())
      .then((result) => {
        dispatch(setProductDescription(result.plain_text));
       
      });

    }catch(Exception){

    }

  }



  return (
    <div>
      <Navbar/>
     <ItemInfo />
    </div>
  );
}
